import { Briefcase, GraduationCap, FileText, MessageSquare, Image, Download, Globe, ArrowRight } from "lucide-react";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import type { ExtractedData } from "@shared/schema";

interface DataPreviewProps {
  data: ExtractedData;
  onSaveData: () => void;
  onCreateWebsite: () => void;
}

export function DataPreview({ data, onSaveData, onCreateWebsite }: DataPreviewProps) {
  const experienceCount = data.experience?.length ?? 0;
  const educationCount = data.education?.length ?? 0;
  const featuredPostsCount = data.featuredPosts?.length ?? 0;
  const recommendationsCount = data.recommendations?.length ?? 0;

  const stats = [
    {
      icon: Briefcase,
      label: "Work Experience",
      value: experienceCount,
      suffix: experienceCount === 1 ? "position" : "positions",
    },
    {
      icon: GraduationCap,
      label: "Education",
      value: educationCount,
      suffix: educationCount === 1 ? "entry" : "entries",
    },
    {
      icon: FileText,
      label: "Featured Posts",
      value: featuredPostsCount,
      suffix: `/ 6`,
    },
    {
      icon: MessageSquare,
      label: "Recommendations",
      value: recommendationsCount,
      suffix: "total",
    },
  ];

  const initials = (data.profile?.fullName ?? "")
    .split(" ")
    .filter((n: string) => n.length > 0)
    .map((n: string) => n[0])
    .join("")
    .toUpperCase()
    .slice(0, 2) || "??";

  const profile = data.profile ?? { fullName: "", headline: "", location: "", summary: "", profilePicture: "", headerImage: "" };

  return (
    <div className="min-h-screen flex items-center justify-center px-4 py-12">
      <div className="w-full max-w-3xl space-y-8">
        <Card>
          <CardHeader className="pb-4">
            <div className="flex flex-col sm:flex-row items-center gap-6">
              <Avatar className="w-24 h-24 border-4 border-background shadow-lg">
                <AvatarImage src={profile.profilePicture} alt={profile.fullName} />
                <AvatarFallback className="text-2xl">{initials}</AvatarFallback>
              </Avatar>
              <div className="text-center sm:text-left space-y-2 flex-1">
                <CardTitle className="text-2xl">{profile.fullName}</CardTitle>
                {profile.headline && (
                  <p className="text-muted-foreground">{profile.headline}</p>
                )}
                {profile.location && (
                  <Badge variant="secondary" className="text-xs">
                    {profile.location}
                  </Badge>
                )}
              </div>
              {(profile.profilePicture || profile.headerImage) && (
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Image className="w-4 h-4" />
                  <span>
                    {[profile.profilePicture, profile.headerImage].filter(Boolean).length} images
                  </span>
                  <Badge size="sm" variant="outline">WebP</Badge>
                </div>
              )}
            </div>
          </CardHeader>
          <CardContent>
            {profile.summary && (
              <p className="text-sm text-muted-foreground line-clamp-3">
                {profile.summary}
              </p>
            )}
          </CardContent>
        </Card>

        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {stats.map((stat) => (
            <Card key={stat.label} className="text-center">
              <CardContent className="pt-6">
                <stat.icon className="w-8 h-8 mx-auto text-primary mb-2" />
                <div className="text-2xl font-bold">{stat.value}</div>
                <div className="text-xs text-muted-foreground">
                  {stat.label}
                  <span className="block">{stat.suffix}</span>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        <Card>
          <CardHeader>
            <CardTitle className="text-lg">What would you like to do?</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid md:grid-cols-2 gap-4">
              <Button
                variant="outline"
                size="lg"
                className="h-auto py-6 flex-col gap-3"
                onClick={onSaveData}
                data-testid="button-save-data"
              >
                <Download className="w-8 h-8" />
                <div className="space-y-1">
                  <div className="font-semibold">Save Data Only</div>
                  <div className="text-xs text-muted-foreground font-normal">
                    Download JSON + Images or save to GitHub
                  </div>
                </div>
              </Button>

              <Button
                size="lg"
                className="h-auto py-6 flex-col gap-3"
                onClick={onCreateWebsite}
                data-testid="button-create-website"
              >
                <Globe className="w-8 h-8" />
                <div className="space-y-1">
                  <div className="font-semibold flex items-center gap-2">
                    Create Personal Website
                    <ArrowRight className="w-4 h-4" />
                  </div>
                  <div className="text-xs text-primary-foreground/80 font-normal">
                    Customize design and deploy to web
                  </div>
                </div>
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/url-input-form.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Linkedin, ArrowRight, Sparkles, Globe, Briefcase, GraduationCap, Award } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormMessage,
} from "@/components/ui/form";

const urlSchema = z.object({
  linkedinUrl: z
    .string()
    .min(1, "Please enter a LinkedIn URL")
    .refine(
      (url) => {
        const linkedinPattern = /^(https?:\/\/)?(www\.)?linkedin\.com\/in\/[\w-]+\/?$/;
        return linkedinPattern.test(url);
      },
      "Please enter a valid LinkedIn profile URL (e.g., linkedin.com/in/username)"
    ),
});

type UrlFormValues = z.infer<typeof urlSchema>;

interface UrlInputFormProps {
  onSubmit: (username: string) => void;
  isLoading?: boolean;
}

export function UrlInputForm({ onSubmit, isLoading }: UrlInputFormProps) {
  const [isFocused, setIsFocused] = useState(false);

  const form = useForm<UrlFormValues>({
    resolver: zodResolver(urlSchema),
    defaultValues: {
      linkedinUrl: "",
    },
  });

  const handleSubmit = (values: UrlFormValues) => {
    const match = values.linkedinUrl.match(/linkedin\.com\/in\/([\w-]+)/);
    if (match) {
      onSubmit(match[1]);
    }
  };

  const features = [
    { icon: Briefcase, label: "Work Experience", description: "Complete career history" },
    { icon: GraduationCap, label: "Education", description: "Academic background" },
    { icon: Award, label: "Certifications", description: "Professional credentials" },
    { icon: Globe, label: "Bilingual Sites", description: "English & Arabic support" },
  ];

  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-4 py-12">
      <div className="w-full max-w-2xl mx-auto text-center space-y-8">
        <div className="space-y-4">
          <div className="inline-flex items-center justify-center w-20 h-20 rounded-full bg-primary/10 mb-4">
            <Linkedin className="w-10 h-10 text-primary" />
          </div>
          <h1 className="text-4xl md:text-5xl font-bold tracking-tight">
            LinkedIn to{" "}
            <span className="bg-gradient-to-r from-primary to-primary/60 bg-clip-text text-transparent">
              Personal Brand
            </span>
          </h1>
          <p className="text-lg text-muted-foreground max-w-xl mx-auto">
            Transform your LinkedIn profile into a stunning, professional website in minutes.
            Extract your data, customize the design, and deploy with one click.
          </p>
        </div>

        <Card className={`transition-all duration-300 ${isFocused ? "ring-2 ring-primary/50" : ""}`}>
          <CardContent className="p-6">
            <Form {...form}>
              <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="linkedinUrl"
                  render={({ field }) => (
                    <FormItem>
                      <FormControl>
                        <div className="relative">
                          <Linkedin className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-muted-foreground" />
                          <Input
                            {...field}
                            placeholder="https://linkedin.com/in/username"
                            className="pl-12 pr-4 h-14 text-lg"
                            onFocus={() => setIsFocused(true)}
                            onBlur={() => setIsFocused(false)}
                            data-testid="input-linkedin-url"
                          />
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <Button
                  type="submit"
                  size="lg"
                  className="w-full h-14 text-lg gap-2"
                  disabled={isLoading}
                  data-testid="button-extract"
                >
                  {isLoading ? (
                    <>
                      <Sparkles className="w-5 h-5 animate-pulse" />
                      Extracting Data...
                    </>
                  ) : (
                    <>
                      Extract My Data
                      <ArrowRight className="w-5 h-5" />
                    </>
                  )}
                </Button>
              </form>
            </Form>
            <p className="text-sm text-muted-foreground mt-4">
              Example: linkedin.com/in/williamhgates
            </p>
          </CardContent>
        </Card>

        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 pt-8">
          {features.map((feature) => (
            <div
              key={feature.label}
              className="flex flex-col items-center gap-2 p-4 rounded-lg bg-muted/50"
            >
              <feature.icon className="w-6 h-6 text-primary" />
              <span className="font-medium text-sm">{feature.label}</span>
              <span className="text-xs text-muted-foreground">{feature.description}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

