client/src/components/wizard/portfolio-s.content:
import { useState } from "react";
import { Plus, Trash2, ExternalLink, Star, Edit } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent } from "@/components/ui/card";
import { Switch } from "@/components/ui/switch";
import type { PortfolioProject } from "@shared/schema";

interface PortfolioStepProps {
  projects: PortfolioProject[];
  onChange: (projects: PortfolioProject[]) => void;
}

export function PortfolioStep({ projects, onChange }: PortfolioStepProps) {
  const [showForm, setShowForm] = useState(false);
  const [editingIndex, setEditingIndex] = useState<number | null>(null);
  const [formData, setFormData] = useState<PortfolioProject>({
    id: "",
    title: "",
    description: "",
    image: "",
    technologies: [],
    liveUrl: "",
    sourceUrl: "",
    featured: false,
  });
  const [techInput, setTechInput] = useState("");

  const resetForm = () => {
    setFormData({
      id: "",
      title: "",
      description: "",
      image: "",
      technologies: [],
      liveUrl: "",
      sourceUrl: "",
      featured: false,
    });
    setTechInput("");
    setShowForm(false);
    setEditingIndex(null);
  };

  const handleSaveProject = () => {
    if (!formData.title.trim()) return;

    const newProjects = [...projects];
    const projectToSave = {
      ...formData,
      id: formData.id || `project-${Date.now()}`,
    };

    if (editingIndex !== null) {
      newProjects[editingIndex] = projectToSave;
    } else {
      newProjects.push(projectToSave);
    }
    onChange(newProjects);
    resetForm();
  };

  const handleEditProject = (index: number) => {
    const project = projects[index];
    setFormData(project);
    setTechInput(project.technologies?.join(", ") || "");
    setEditingIndex(index);
    setShowForm(true);
  };

  const handleDeleteProject = (index: number) => {
    const newProjects = projects.filter((_, i) => i !== index);
    onChange(newProjects);
  };

  const handleTechInputChange = (value: string) => {
    setTechInput(value);
    const technologies = value.split(",").map((t: string) => t.trim()).filter((t: string) => t);
    setFormData(prev => ({ ...prev, technologies }));
  };

  return (
    <div className="space-y-6">
      <div className="text-center mb-6">
        <h3 className="text-lg font-semibold mb-2" data-testid="text-step-title">Portfolio Projects</h3>
        <p className="text-sm text-muted-foreground">
          Add projects to showcase your work beyond your LinkedIn experience
        </p>
      </div>

      {projects.length > 0 && (
        <div className="space-y-3">
          {projects.map((project, index) => (
            <Card key={project.id || index} className="overflow-visible">
              <CardContent className="p-4">
                <div className="flex items-start justify-between gap-3">
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2">
                      <h4 className="font-medium truncate" data-testid={`text-project-title-${index}`}>
                        {project.title}
                      </h4>
                      {project.featured && (
                        <Star className="w-4 h-4 text-amber-500 fill-amber-500 flex-shrink-0" />
                      )}
                    </div>
                    {project.description && (
                      <p className="text-sm text-muted-foreground mt-1 line-clamp-2">
                        {project.description}
                      </p>
                    )}
                    {project.technologies && project.technologies.length > 0 && (
                      <div className="flex flex-wrap gap-1 mt-2">
                        {project.technologies.map((tech: string, techIndex: number) => (
                          <span
                            key={techIndex}
                            className="text-xs px-2 py-0.5 bg-muted rounded-md"
                          >
                            {tech}
                          </span>
                        ))}
                      </div>
                    )}
                  </div>
                  <div className="flex gap-1 flex-shrink-0">
                    {project.liveUrl && (
                      <Button
                        size="icon"
                        variant="ghost"
                        onClick={() => window.open(project.liveUrl, "_blank")}
                        data-testid={`button-project-link-${index}`}
                      >
                        <ExternalLink className="w-4 h-4" />
                      </Button>
                    )}
                    <Button
                      size="icon"
                      variant="ghost"
                      onClick={() => handleEditProject(index)}
                      data-testid={`button-edit-project-${index}`}
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    <Button
                      size="icon"
                      variant="ghost"
                      onClick={() => handleDeleteProject(index)}
                      data-testid={`button-delete-project-${index}`}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {showForm ? (
        <Card className="overflow-visible">
          <CardContent className="p-4 space-y-4">
            <div className="space-y-2">
              <Label htmlFor="project-title">Project Title *</Label>
              <Input
                id="project-title"
                value={formData.title}
                onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                placeholder="e.g., E-commerce Platform"
                data-testid="input-project-title"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="project-description">Description</Label>
              <Textarea
                id="project-description"
                value={formData.description || ""}
                onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                placeholder="Brief description of the project..."
                className="resize-none"
                rows={3}
                data-testid="input-project-description"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="project-live-url">Live Demo URL</Label>
              <Input
                id="project-live-url"
                type="url"
                value={formData.liveUrl || ""}
                onChange={(e) => setFormData(prev => ({ ...prev, liveUrl: e.target.value }))}
                placeholder="https://example.com"
                data-testid="input-project-live-url"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="project-source-url">Source Code URL</Label>
              <Input
                id="project-source-url"
                type="url"
                value={formData.sourceUrl || ""}
                onChange={(e) => setFormData(prev => ({ ...prev, sourceUrl: e.target.value }))}
                placeholder="https://github.com/user/project"
                data-testid="input-project-source-url"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="project-image">Image URL</Label>
              <Input
                id="project-image"
                type="url"
                value={formData.image || ""}
                onChange={(e) => setFormData(prev => ({ ...prev, image: e.target.value }))}
                placeholder="https://example.com/image.jpg"
                data-testid="input-project-image"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="project-technologies">Technologies (comma-separated)</Label>
              <Input
                id="project-technologies"
                value={techInput}
                onChange={(e) => handleTechInputChange(e.target.value)}
                placeholder="React, Node.js, AWS"
                data-testid="input-project-technologies"
              />
            </div>

            <div className="flex items-center justify-between">
              <Label htmlFor="project-featured" className="cursor-pointer">Featured Project</Label>
              <Switch
                id="project-featured"
                checked={formData.featured}
                onCheckedChange={(checked) => setFormData(prev => ({ ...prev, featured: checked }))}
                data-testid="switch-project-featured"
              />
            </div>

            <div className="flex gap-2 justify-end">
              <Button variant="outline" onClick={resetForm} data-testid="button-cancel-project">
                Cancel
              </Button>
              <Button onClick={handleSaveProject} disabled={!formData.title.trim()} data-testid="button-save-project">
                {editingIndex !== null ? "Update" : "Add"} Project
              </Button>
            </div>
          </CardContent>
        </Card>
      ) : (
        <Button
          variant="outline"
          className="w-full"
          onClick={() => setShowForm(true)}
          data-testid="button-add-project"
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Portfolio Project
        </Button>
      )}

      {projects.length === 0 && !showForm && (
        <p className="text-center text-sm text-muted-foreground py-4">
          No projects added yet. Portfolio section is optional.
        </p>
      )}
    </div>
  );
}

server/image-processor.ts.content:
import sharp from "sharp";
import type { ExtractedData } from "@shared/schema";

interface ProcessedImage {
  original: Buffer;
  webp: Buffer;
  sizes: {
    mobile: Buffer;
    tablet: Buffer;
    desktop: Buffer;
  };
  filename: string;
  mimeType: string;
}

interface ImageProcessingResult {
  profileImage?: ProcessedImage;
  headerImage?: ProcessedImage;
  companyLogos: ProcessedImage[];
  schoolLogos: ProcessedImage[];
  recommenderImages: ProcessedImage[];
}

// Image size configurations for responsive images
const SIZES = {
  mobile: { width: 320, quality: 80 },
  tablet: { width: 640, quality: 85 },
  desktop: { width: 1024, quality: 90 },
};

const PROFILE_SIZES = {
  mobile: { width: 80, height: 80, quality: 85 },
  tablet: { width: 120, height: 120, quality: 85 },
  desktop: { width: 160, height: 160, quality: 90 },
};

async function fetchImage(url: string): Promise<Buffer | null> {
  try {
    const response = await fetch(url);
    if (!response.ok) return null;
    const arrayBuffer = await response.arrayBuffer();
    return Buffer.from(arrayBuffer);
  } catch (error) {
    console.error(`Failed to fetch image from ${url}:`, error);
    return null;
  }
}

async function processImage(
  imageBuffer: Buffer,
  filename: string,
  isProfile: boolean = false
): Promise<ProcessedImage> {
  const sizesConfig = isProfile ? PROFILE_SIZES : SIZES;

  // Create WebP version
  const webp = await sharp(imageBuffer)
    .webp({ quality: 85 })
    .toBuffer();

  // Create responsive sizes
  const mobile = await sharp(imageBuffer)
    .resize(sizesConfig.mobile.width, isProfile ? (sizesConfig.mobile as any).height : undefined, {
      fit: isProfile ? 'cover' : 'inside',
      withoutEnlargement: true,
    })
    .webp({ quality: sizesConfig.mobile.quality })
    .toBuffer();

  const tablet = await sharp(imageBuffer)
    .resize(sizesConfig.tablet.width, isProfile ? (sizesConfig.tablet as any).height : undefined, {
      fit: isProfile ? 'cover' : 'inside',
      withoutEnlargement: true,
    })
    .webp({ quality: sizesConfig.tablet.quality })
    .toBuffer();

  const desktop = await sharp(imageBuffer)
    .resize(sizesConfig.desktop.width, isProfile ? (sizesConfig.desktop as any).height : undefined, {
      fit: isProfile ? 'cover' : 'inside',
      withoutEnlargement: true,
    })
    .webp({ quality: sizesConfig.desktop.quality })
    .toBuffer();

  return {
    original: imageBuffer,
    webp,
    sizes: { mobile, tablet, desktop },
    filename,
    mimeType: 'image/webp',
  };
}

export async function processAllImages(data: ExtractedData): Promise<ImageProcessingResult> {
  const result: ImageProcessingResult = {
    companyLogos: [],
    schoolLogos: [],
    recommenderImages: [],
  };

  // Process profile picture
  if (data.profile.profilePicture) {
    const buffer = await fetchImage(data.profile.profilePicture);
    if (buffer) {
      result.profileImage = await processImage(buffer, 'profile', true);
    }
  }

  // Process header image
  if (data.profile.headerImage) {
    const buffer = await fetchImage(data.profile.headerImage);
    if (buffer) {
      result.headerImage = await processImage(buffer, 'header', false);
    }
  }

  // Process company logos
  for (const exp of data.experience) {
    if (exp.companyLogo) {
      const buffer = await fetchImage(exp.companyLogo);
      if (buffer) {
        const processed = await processImage(buffer, `company-${exp.id}`, false);
        result.companyLogos.push(processed);
      }
    }
  }

  // Process school logos
  for (const edu of data.education) {
    if (edu.schoolLogo) {
      const buffer = await fetchImage(edu.schoolLogo);
      if (buffer) {
        const processed = await processImage(buffer, `school-${edu.id}`, false);
        result.schoolLogos.push(processed);
      }
    }
  }

  // Process recommender profile pictures
  for (const rec of data.recommendations) {
    if (rec.recommenderProfilePicture) {
      const buffer = await fetchImage(rec.recommenderProfilePicture);
      if (buffer) {
        const processed = await processImage(buffer, `recommender-${rec.id}`, true);
        result.recommenderImages.push(processed);
      }
    }
  }

  return result;
}

export async function createOptimizedImageBundle(
  data: ExtractedData
): Promise<Map<string, Buffer>> {
  const images = new Map<string, Buffer>();
  const processed = await processAllImages(data);

  // Add profile image variants
  if (processed.profileImage) {
    images.set('images/profile.webp', processed.profileImage.webp);
    images.set('images/profile-mobile.webp', processed.profileImage.sizes.mobile);
    images.set('images/profile-tablet.webp', processed.profileImage.sizes.tablet);
    images.set('images/profile-desktop.webp', processed.profileImage.sizes.desktop);
  }

  // Add header image variants
  if (processed.headerImage) {
    images.set('images/header.webp', processed.headerImage.webp);
    images.set('images/header-mobile.webp', processed.headerImage.sizes.mobile);
    images.set('images/header-tablet.webp', processed.headerImage.sizes.tablet);
    images.set('images/header-desktop.webp', processed.headerImage.sizes.desktop);
  }

  // Add company logos
  for (const logo of processed.companyLogos) {
    images.set(`images/${logo.filename}.webp`, logo.webp);
  }

  // Add school logos
  for (const logo of processed.schoolLogos) {
    images.set(`images/${logo.filename}.webp`, logo.webp);
  }

  // Add recommender images
  for (const img of processed.recommenderImages) {
    images.set(`images/${img.filename}.webp`, img.webp);
  }

  return images;
}

export async function getOptimizedImageSrcSet(
  originalUrl: string,
  type: 'profile' | 'header' | 'logo'
): Promise<string> {
  const baseName = type;
  const sizes = type === 'logo' 
    ? '64w' 
    : '(max-width: 480px) 320w, (max-width: 768px) 640w, 1024w';

  return `
    images/${baseName}-mobile.webp 320w,
    images/${baseName}-tablet.webp 640w,
    images/${baseName}-desktop.webp 1024w
  `.trim();
}

client/src/components/kpi-dashboard.tsx.content:
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { 
  CheckCircle2, 
  TrendingUp, 
  Users, 
  Smartphone, 
  FileText,
  BarChart3,
  Eye,
  MousePointerClick,
  Clock
} from "lucide-react";
import type { ExtractedData, WebsiteConfig } from "@shared/schema";

interface KpiDashboardProps {
  extractedData: ExtractedData | null;
  websiteConfig: WebsiteConfig | null;
}

interface MetricCardProps {
  title: string;
  value: string | number;
  subtitle?: string;
  icon: typeof CheckCircle2;
  trend?: "up" | "down" | "neutral";
  color?: "default" | "success" | "warning" | "destructive";
  "data-testid"?: string;
}

function MetricCard({ title, value, subtitle, icon: Icon, trend, color = "default", "data-testid": testId }: MetricCardProps) {
  const colorClasses = {
    default: "text-foreground",
    success: "text-green-600 dark:text-green-400",
    warning: "text-yellow-600 dark:text-yellow-400",
    destructive: "text-red-600 dark:text-red-400",
  };

  return (
    <Card data-testid={testId}>
      <CardContent className="pt-6">
        <div className="flex items-center justify-between gap-4">
          <div className="flex-1 min-w-0">
            <p className="text-sm text-muted-foreground truncate">{title}</p>
            <p className={`text-2xl font-bold ${colorClasses[color]}`}>{value}</p>
            {subtitle && <p className="text-xs text-muted-foreground mt-1">{subtitle}</p>}
          </div>
          <div className="shrink-0">
            <div className="p-3 rounded-full bg-muted">
              <Icon className="w-5 h-5 text-muted-foreground" />
            </div>
          </div>
        </div>
        {trend && (
          <div className="mt-2">
            <Badge variant={trend === "up" ? "default" : trend === "down" ? "destructive" : "secondary"} className="text-xs">
              {trend === "up" ? "Trending Up" : trend === "down" ? "Needs Attention" : "Stable"}
            </Badge>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

function QualityScoreCard({ score, label, details }: { score: number; label: string; details: string[] }) {
  const getScoreColor = (s: number) => {
    if (s >= 80) return "text-green-600 dark:text-green-400";
    if (s >= 60) return "text-yellow-600 dark:text-yellow-400";
    return "text-red-600 dark:text-red-400";
  };

  const getProgressColor = (s: number) => {
    if (s >= 80) return "bg-green-600";
    if (s >= 60) return "bg-yellow-600";
    return "bg-red-600";
  };

  return (
    <Card>
      <CardHeader className="pb-2">
        <CardTitle className="text-sm font-medium">{label}</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="flex items-end gap-2 mb-2">
          <span className={`text-3xl font-bold ${getScoreColor(score)}`}>{score}</span>
          <span className="text-sm text-muted-foreground mb-1">/100</span>
        </div>
        <Progress value={score} className="h-2 mb-3" />
        <ul className="space-y-1">
          {details.map((detail, idx) => (
            <li key={idx} className="text-xs text-muted-foreground flex items-center gap-1">
              <CheckCircle2 className="w-3 h-3 text-green-500" />
              {detail}
            </li>
          ))}
        </ul>
      </CardContent>
    </Card>
  );
}

export function KpiDashboard({ extractedData, websiteConfig }: KpiDashboardProps) {
  const calculateExtractionScore = () => {
    if (!extractedData) return { score: 0, details: [] };

    let score = 0;
    const details: string[] = [];

    if (extractedData.profile?.fullName) {
      score += 15;
      details.push("Profile info extracted");
    }
    if (extractedData.profile?.summary) {
      score += 10;
      details.push("Bio/summary included");
    }
    if (extractedData.experience && extractedData.experience.length > 0) {
      score += 20;
      details.push(`${extractedData.experience.length} work experiences`);
    }
    if (extractedData.education && extractedData.education.length > 0) {
      score += 15;
      details.push(`${extractedData.education.length} education entries`);
    }
    if (extractedData.skills && extractedData.skills.length > 0) {
      score += 15;
      details.push(`${extractedData.skills.length} skills listed`);
    }
    if (extractedData.certifications && extractedData.certifications.length > 0) {
      score += 10;
      details.push(`${extractedData.certifications.length} certifications`);
    }
    if (extractedData.recommendations && extractedData.recommendations.length > 0) {
      score += 10;
      details.push(`${extractedData.recommendations.length} recommendations`);
    }
    if (extractedData.featuredPosts && extractedData.featuredPosts.length > 0) {
      score += 5;
      details.push(`${extractedData.featuredPosts.length} featured posts`);
    }

    return { score: Math.min(score, 100), details };
  };

  const calculateWebsiteQuality = () => {
    if (!websiteConfig) return { score: 0, details: [] };

    let score = 40;
    const details: string[] = [];

    details.push("Responsive design enabled");

    if (websiteConfig.language === "both") {
      score += 20;
      details.push("Bilingual support (EN/AR)");
    } else {
      score += 10;
      details.push(`Single language: ${websiteConfig.language}`);
    }

    if (websiteConfig.aestheticLevel === "premium") {
      score += 20;
      details.push("Premium GSAP animations");
    } else if (websiteConfig.aestheticLevel === "enhanced") {
      score += 15;
      details.push("Enhanced AOS animations");
    } else {
      score += 5;
      details.push("Standard styling");
    }

    if (websiteConfig.whatsappNumber || websiteConfig.phoneNumber) {
      score += 10;
      details.push("Contact buttons enabled");
    }

    if (websiteConfig.portfolioProjects && websiteConfig.portfolioProjects.length > 0) {
      score += 10;
      details.push(`${websiteConfig.portfolioProjects.length} portfolio projects`);
    }

    if (websiteConfig.analytics?.googleAnalyticsId) {
      score += 10;
      details.push("Analytics tracking enabled");
    }

    return { score: Math.min(score, 100), details };
  };

  const calculateMobileScore = () => {
    let score = 85;
    const details: string[] = [];

    details.push("Mobile-first responsive layout");
    details.push("Touch-friendly navigation");
    details.push("Optimized font sizes");

    if (websiteConfig?.aestheticLevel !== "premium") {
      score += 10;
      details.push("Lightweight animations");
    }

    return { score: Math.min(score, 100), details };
  };

  const extractionMetrics = calculateExtractionScore();
  const websiteQuality = calculateWebsiteQuality();
  const mobileScore = calculateMobileScore();

  const totalEngagement = extractedData?.featuredPosts?.reduce((sum, post) => {
    const engagement = post.engagement;
    return sum + (engagement?.likes || 0) + (engagement?.comments || 0) + (engagement?.shares || 0);
  }, 0) || 0;

  return (
    <div className="space-y-6">
      <div className="flex items-center gap-2">
        <BarChart3 className="w-6 h-6 text-primary" />
        <h2 className="text-2xl font-bold" data-testid="text-kpi-title">Website Quality Dashboard</h2>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <MetricCard
          title="Data Extraction"
          value={`${extractionMetrics.score}%`}
          subtitle="Profile completeness"
          icon={CheckCircle2}
          color={extractionMetrics.score >= 80 ? "success" : extractionMetrics.score >= 50 ? "warning" : "destructive"}
          data-testid="metric-extraction"
        />
        <MetricCard
          title="Featured Content"
          value={extractedData?.featuredPosts?.length || 0}
          subtitle={`${totalEngagement} total engagements`}
          icon={FileText}
          color="default"
          data-testid="metric-featured"
        />
        <MetricCard
          title="Skills Showcased"
          value={extractedData?.skills?.length || 0}
          subtitle="Professional skills"
          icon={TrendingUp}
          color="success"
          data-testid="metric-skills"
        />
        <MetricCard
          title="Recommendations"
          value={extractedData?.recommendations?.length || 0}
          subtitle="Social proof"
          icon={Users}
          color={extractedData?.recommendations?.length ? "success" : "warning"}
          data-testid="metric-recommendations"
        />
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <QualityScoreCard
          score={extractionMetrics.score}
          label="Data Extraction Score"
          details={extractionMetrics.details}
        />
        <QualityScoreCard
          score={websiteQuality.score}
          label="Website Quality Score"
          details={websiteQuality.details}
        />
        <QualityScoreCard
          score={mobileScore.score}
          label="Mobile Responsiveness"
          details={mobileScore.details}
        />
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="text-lg flex items-center gap-2">
            <Eye className="w-5 h-5" />
            Engagement Insights
          </CardTitle>
          <CardDescription>
            Performance metrics for your featured content
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="text-center p-4 rounded-lg bg-muted/50">
              <p className="text-2xl font-bold text-foreground">
                {extractedData?.featuredPosts?.reduce((sum, p) => sum + (p.engagement?.likes || 0), 0) || 0}
              </p>
              <p className="text-xs text-muted-foreground mt-1">Total Likes</p>
            </div>
            <div className="text-center p-4 rounded-lg bg-muted/50">
              <p className="text-2xl font-bold text-foreground">
                {extractedData?.featuredPosts?.reduce((sum, p) => sum + (p.engagement?.comments || 0), 0) || 0}
              </p>
              <p className="text-xs text-muted-foreground mt-1">Total Comments</p>
            </div>
            <div className="text-center p-4 rounded-lg bg-muted/50">
              <p className="text-2xl font-bold text-foreground">
                {extractedData?.featuredPosts?.reduce((sum, p) => sum + (p.engagement?.shares || 0), 0) || 0}
              </p>
              <p className="text-xs text-muted-foreground mt-1">Total Shares</p>
            </div>
            <div className="text-center p-4 rounded-lg bg-muted/50">
              <p className="text-2xl font-bold text-foreground">
                {extractedData?.experience?.length || 0}
              </p>
              <p className="text-xs text-muted-foreground mt-1">Years of Experience</p>
            </div>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle className="text-lg flex items-center gap-2">
            <Smartphone className="w-5 h-5" />
            Website Features
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex flex-wrap gap-2">
            <Badge variant="secondary">Responsive Design</Badge>
            <Badge variant="secondary">SEO Optimized</Badge>
            {websiteConfig?.language === "both" && <Badge variant="secondary">Bilingual (EN/AR)</Badge>}
            {websiteConfig?.aestheticLevel === "premium" && <Badge variant="secondary">GSAP Animations</Badge>}
            {websiteConfig?.aestheticLevel === "enhanced" && <Badge variant="secondary">AOS Animations</Badge>}
            {(websiteConfig?.whatsappNumber || websiteConfig?.phoneNumber) && <Badge variant="secondary">Contact Buttons</Badge>}
            {extractedData?.featuredPosts && extractedData.featuredPosts.length > 0 && <Badge variant="secondary">Swiper Carousel</Badge>}
            {websiteConfig?.analytics?.googleAnalyticsId && <Badge variant="secondary">Analytics</Badge>}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}
